import React, { Component } from "react";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

class Auth extends Component {
  state = {
    Auth: [],
    email: "",
    password: "",
  };

//   componentDidMount() {
//     this.loadBooks();
//   }

  validateAuth = (email,password) => {
    API.getAuth(email,password)
      .then(res =>
        this.state.history.push("/login")
      )
      .catch(err => console.log(err));
  };
 
  signInChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  
  signUpSubmit = event => {
    event.preventDefault();
    if (this.state.email && this.state.password) {
      API.saveAuth({
        email: this.state.email,
        password: this.state.password
      })
        .then(console.log("created successfully"))
        .catch(err => console.log(err));
    }
  };

  render() {
    return (      
        <div >
            <Grid container direction="column" spacing={3}>
                <Grid item xs={8} sm={4}>
                    <Paper >
                    <h3>Sign up</h3>
                    <form  noValidate autoComplete="off">
                    <TextField id="standard-basic" required fullWidth label="Enter Email" type="email" 
                    value={this.state.email}
                    onChange={this.signInChange}
                    name="email" 
                     />
                    <TextField id="standard-basic" required fullWidth label="Enter Password" type="password" 
                    onChange={this.signInChange}
                    name="password" 
                    value={this.state.password} />
                    <Button onClick={this.signUpSubmit}  fullWidth variant="contained" color="primary" >Log in</Button> 
                    </form>
                    </Paper>
                </Grid>        
            </Grid>
        </div>

        
    //   <Container fluid>
    //     <Row>
    //       <Col size="md-6">
    //         <Jumbotron>
    //           <h1>Please</h1>
    //         </Jumbotron>
    //         <form>
    //           <Input
    //             value={this.state.title}
    //             onChange={this.handleInputChange}
    //             name="title"
    //             placeholder="Title (required)"
    //           />
    //           <Input
    //             value={this.state.author}
    //             onChange={this.handleInputChange}
    //             name="author"
    //             placeholder="Author (required)"
    //           />
    //           <TextArea
    //             value={this.state.synopsis}
    //             onChange={this.handleInputChange}
    //             name="synopsis"
    //             placeholder="Synopsis (Optional)"
    //           />
    //           <FormBtn
    //             disabled={!(this.state.author && this.state.title)}
    //             onClick={this.handleFormSubmit}
    //           >
    //             Submit Book
    //           </FormBtn>
    //         </form>
    //       </Col>
    //       <Col size="md-6 sm-12">
    //         <Jumbotron>
    //           <h1>Books On My List</h1>
    //         </Jumbotron>
    //         {this.state.books.length ? (
    //           <List>
    //             {this.state.books.map(book => (
    //               <ListItem key={book._id}>
    //                 <Link to={"/books/" + book._id}>
    //                   <strong>
    //                     {book.title} by {book.author}
    //                   </strong>
    //                 </Link>
    //                 <DeleteBtn onClick={() => this.deleteBook(book._id)} />
    //               </ListItem>
    //             ))}
    //           </List>
    //         ) : (
    //           <h3>No Results to Display</h3>
    //         )}
    //       </Col>
    //     </Row>
    //   </Container>
    );
  }
}

export default Auth;
