import React from "react";
import emailjs from 'emailjs-com';
import { fade, makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles(theme => ({
  root: {
     flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(10),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  image: {
    padding: 60,
    margin: 30,
    backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundSize: 'cover',
    backgroundPosition: 'right',

  },
  emailformlayout: {
    width: 300,
    height: 200,

  },
  emailinputlayout : {
    textAlign: "right",
    height: 30,

  },
}));

export default function Friend(props) {

  const classes = useStyles();

  function sendEmail(e) {
    
    e.preventDefault();

    emailjs.sendForm('default_service', 'reach_F', e.target, 'user_6E17hEsOwkfjk6uHyijD7')
    .then((res) => {console.log(res.text);
    }, (error) => {console.log(error.text);
    });
  }
  function logoutSubmit (){

    props.history.push("/");
                      
  }

  return (
    <div className={classes.root}>
      <Button onClick={logoutSubmit} className={classes.button} variant="contained" color="primary" >Log out</Button> 
      <Grid  className={classes.image} container direction="column" spacing={3}>
            <Grid item xs={8} sm={4}>
              <Paper className={classes.paper}>
                <h3>Reach People</h3>
                <form className={classes.root} onSubmit={sendEmail}>
                  <TextField id="emainsignup" required fullWidth label="Enter Email" type="email" 
                    value={this.state.email}
                    onChange={this.signInChange}
                    name="email" 
                      />
                    <label className={classes.emaillabellayout}>name</label>  
                    <input className={classes.emailinputlayout} type="text" name="user_name" /> 
                    <br />
                    <label className={classes.emaillabellayout}>Email</label>            
                    <input className={classes.emailinputlayout} type="email" name="user_email" />
                    <br />
                    <label className={classes.emaillabellayout}>Message</label>            
                    <textarea className={classes.emailinputlayout} name="message" />            
                </form>
            </Paper>
        </Grid>
      </Grid>

    </div>
  );
// }


