import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, Redirect, withRouter } from "react-router-dom";
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import CreateTodo from "./components/create-todo.components";
import EditTodo from "./components/edit-todo.components";
import TodosList from "./components/todos-list.components";
// import Loginform from "./components/loginform";
import { Nav } from 'react-bootstrap';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';


const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  button: {
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 3,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 48,
    padding: '0 30px',
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },

}));
const fakeAuth = {
  isAuthenticated: false,
  authenticate(cb) {
    this.isAuthenticated = true 
    setTimeout(cb, 100)
  },
  signout(cb) {
    this.isAuthenticated = false
    setTimeout(cb, 100)    
  }
};
const classes = useStyles();

class Login extends Component {
  state = {
    redirectToReferrer: false,
    email: "",
    password: ""
  };

  loginSubmit = event => {
    event.preventDefault();
    if ((this.state.email === "arthiatsr@gmail.com") && (this.state.password === "america")) {


    } else {
      alert(`Login Failed`);
    }

    this.setState({
      firstName: "",
      lastName: "",
      password: ""
    });
  };

  login = () => {
    fakeAuth.authenticate(() => {
      this.setState(() => ({
        redirectToReferrer: true

      }))
    })
  }

  render() {
    const { redirectToReferrer } = this.state;
    const { from } = this.props.location.state || { from: { pathname: '/' }};
    if(redirectToReferrer === true){
      return(
        <Redirect to={from} />
      )
    };
    return (
      <div>
        <p>You must login to view this page at {from.pathname} </p>
        <button onClick={this.login}>Login</button>
      </div>
    )
  }
}
const Public = () => <h3>Public</h3>;
const Protected = () => <h3>Protected</h3>;

const PrivateRoute = ({ component: Component, ...rest }) => (
  <Route {...rest} render={(props) => (
    fakeAuth.isAuthenticated === true 
    ? <Component {...props} /> 
    : <Redirect to= {{
        pathname: '/login',
        state: { from: props.location }
    }} />
  )} />
);

const AuthButton = withRouter(({ history }) => (
  fakeAuth.isAuthenticated ? (<p>Hi There!!! <button onClick={() => {
    fakeAuth.signout(() => history.push('/'))
  }}>Log out</button></p>)
    : (<p>You are not Logged in</p>)

))

class App extends Component {

  render() {
    return (
      <Router>
        <div className="container">
          <AuthButton />
          <div>
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <img src="https://img.pngio.com/arbonne-png-free-arbonnepng-transparent-images-33881-pngio-arbonne-logo-transparent-background-260_260.png" width="30" height="30" alt="Arbonne Png & Free Arbonne.png Transparent Images #33881 - PNGio"></img>
            <Link to="/" className="navbar-brand">App</Link>
            <Nav defaultActiveKey="/" as="ul">
              <Nav.Item as="li">
                <Nav.Link href="/">Reminders</Nav.Link>
              </Nav.Item>
              <Nav.Item as="li">
                <Nav.Link href="/create">Add</Nav.Link>
              </Nav.Item>
            </Nav>
            </nav>
            </div>
            <div>
              <ul>
                <li><Link to='/public'>Public Page</Link></li>
                <li><Link to='/protected'>Protected Page</Link></li>              
              </ul>
            </div>
            {/* <div className="collapse nav-collapse">
              <ul className="navbar-nav">
                <li className="navbar-item">
                  <Link to="/" className="nav-link">Todos</Link>
                </li>
                <li className="navbar-item">
                  <Link to="/create" className="nav-link">Create</Link>
                </li>
              </ul>
            </div> */}
          <div className={classes.root}>
            <Grid container direction="column" spacing={3}>
              <Grid item xs={8} sm={4}>
                <Paper className={classes.paper}>
                <form className={classes.root} noValidate autoComplete="off">
                  <TextField id="standard-basic" required fullWidth label="Enter Email"   />
                  <TextField id="standard-basic" required fullWidth label="Enter Password"   />
                  <Button type="submit" onClick={this.loginSubmit} className={classes.button} fullWidth variant="contained" color="primary" noValidate >Log in</Button> 
                </form>
                </Paper>
              </Grid>        
            </Grid>
          </div>
          <Route path="/" exact component={TodosList} />
          <Route path="/edit/:id" component={EditTodo} />
          <Route path="/create" component={CreateTodo} />
          <Route path="/public" component={Public} />
          <Route path="/login" component={Login} />
          <PrivateRoute path="/protected" component={Protected} />
        </div>

      </Router>
    );
  }
}

export default App;
