import React, { useState} from "react";
import emailjs from 'emailjs-com';
import { fade, makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles(theme => ({
  root: {
     flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    padding: 30,
  },
  image: {
    padding: 60,
    margin: 30,
    backgroundImage: 'url(https://source.unsplash.com/random)',
    backgroundSize: 'cover',
    backgroundPosition: 'right',

  },
}));

export default function Friend(props) {

  const [value, setValue] = useState("initialValue");
  
  const classes = useStyles();

  function sendEmail(e) {
    
    e.preventDefault();

    emailjs.sendForm('default_service', 'reach_friend', e.target, 'user_6E17hEsOwkfjk6uHyijD7')
    .then((res) => {console.log(res.text); 
    }, (error) => {console.log(error.text);
    });
  }
  function logoutSubmit (){

    props.history.push("/");
                      
  }
  //////
  window.render = () => { 
    ReactDOM.render(<emailFriend/>, document.getElementById('root'));
  }
  
  // const emailFriend = () => {
    form = { }  
  
    update = (e) => {
      form[e.target.name] = e.target 
    }
  
    clearform = (e) => {
      let value = form["user_name"].value 
      console.log(value)
      form["user_name"].value = "" 
      window.render() 
    }
/////
  return (
    <div className="Friend">
      <Button onClick={logoutSubmit} className={classes.button} variant="contained" color="primary" >Log out</Button> 
      
                <form className="contact-form" onSubmit={sendEmail}>
                    <label>name</label>  
                    <input type="text" name="user_name" /> 
                    <br />
                    <label>Email</label>            
                    <input id="emailid" type="email" name="user_email" />
                    <br />
                    <label>Message</label>            
                    <textarea name="message" /> 
                    <input type="submit" value="Send"  onClick={clearform}/>           
                </form>
    </div>
  );
 }

