import React, { Component } from 'react';
//import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { Redirect } from 'react-router-dom';

class LoginForm extends Component {

  state = {
    email: "",
    password: "",
    isAuthenticated: false
  };  

  loginInput = event => {
    let value = event.target.value;
    const name = event.target.name;

    this.setState({
      [name]: value      
    });
  };

  loginSubmit = event => {
    event.preventDefault();

    if((this.state.email === "arthiatsr@gmail.com") && (this.state.password === "america")){
      console.log("hi");
      isAuthenticated = true;
    }
    else{
      alert(`Login Failed`);
    }

    this.setState({
      email: "",
      password: ""
    });
  };

  
  render() {
      
  return (
    // <div>Hi</div>
    // <div className={classes.root}>
    //   <Grid container direction="column" spacing={3}>
    //     <Grid item xs={8} sm={4}>
    //       <Paper className={classes.paper}>
    //       <form className={classes.root} noValidate autoComplete="off">
    //         <TextField id="emailid" required fullWidth label="Enter Email"  />
    //         <TextField id="passwordid" required fullWidth label="Enter Password"  />
    //         <Button type="submit" onClick={this.loginSubmit} className={classes.button} fullWidth variant="contained" color="primary" noValidate >Log in</Button>
    //       </form>
    //       </Paper>
    //     </Grid>        
    //   </Grid>
    // </div>
    <div>
      if(isAuthenticated) {
         <Redirect to='/' />
      }
      <Grid container direction="column" spacing={3}>
        <Grid item xs={8} sm={4}>
          <Paper >
          <form  noValidate autoComplete="off">
            <TextField id="emailid" required fullWidth label="Enter Email" value={this.state.email}
            name="email" onChange={this.loginInput}
            type="text" />
            <TextField id="passwordid" required fullWidth label="Enter Password" value={this.state.password}
            name="password" onChange={this.loginInput}
            type="password" />
            <Button type="submit" onClick={this.loginSubmit} fullWidth variant="contained" color="primary" noValidate >Log in</Button>
          </form>
          </Paper>
        </Grid>        
      </Grid>
    </div>
  );
 }
  
}

export default LoginForm;
